select * from ( select * from ( select A.*, rownum as RNUM from (
select * from ( select company_code , companyname, business_number from company )c left join ( select memberid, membername, member_code, company_code as company_code2 from member ) m on c.COMPANY_CODE = m.COMPANY_CODE2 join jobopening j on c.COMPANY_CODE = j.COMPANY_CODE
) A )
or jobregdate >#{searchStartDay} and jobregdate < to_date(#{searchEndDay})+1
or localcheck like '%' || #{jobopeningVO.localcheck} ||'%'
or agelimit in
#{age}
or agelimit = #{jobopeningVO.agelimit}
