mapper.select:
select * from ( select A.*, rownum as RNUM from ( select m.ADMINID, n.*, (sysdate-notifydate)*24 as new_Img_Term from manager m join notice n on m.ADMIN_CODE = n.ADMIN_CODE
where ${searchCondition} like '%' || #{searchKeyword} || '%'
order by notify_Code desc ) A ) where RNUM>#{firstRecordIndex} and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update notice
set readcount=readcount+1 
where notify_Code=#{notifyCode}

update notice set notifytitle=#{notifytitle},
	notifycontent=#{notifycontent}
where notify_Code=#{notifyCode}
