mapper.update:
update payment
set progress='결제취소요청'
where to_char(paydate,'yyyy-MM-dd HH:mm:ss')=#{paydate}

update payment
set progress='결제완료'
where payment_code=#{paymentCode}

mapper.select:
	 select p.*, j.jobtitle, (sysdate-p.paydate) as confirmtime
	 from payment p join jobopening j
 	on p.JOBOPENING = j.JOBOPENING
	where to_char(paydate,'yyyy-MM-dd HH:mm:ss')=#{paydate} 
	and member_code=#{memberCode}
	order by paydate desc

mapper.update:
	update payment set progress=#{progressSel}
where payment_code in
	
		#{code}

mapper.select:
select* from ( select A.*, rownum as RNUM from ( select payment_code,paymentway,progress,period,price,paydate, s.servicename, m.memberid from payment p join service s on p.service_code = s.service_code join member m on p.member_code = m.member_code
progress='결제완료'
progress='결제취소'
progress='결제취소요청'
) A ) where RNUM>#{searchVo.firstRecordIndex} and RNUM<=#{searchVo.firstRecordIndex} + #{searchVo.recordCountPerPage}
